sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    isPastDate: function(strDate) {
        var rightnow = new GlideDateTime();
        var testdate = new GlideDateTime(strDate);

        if (testdate.before(rightnow)) {
            return true;
        } else {
            return false;
        }
    },
    isTodayDate: function(strDate) {
        var todadate = new GlideDateTime().getLocalDate();
        var testdate = new GlideDateTime(strDate).getLocalDate();

        if (testdate.compareTo(todadate) == 0) {
            return true;
        } else {
            return false;
        }
    },

    type: 'NeedItUtils'
};
