var GetEmailAddressFromDatabase = Class.create();
GetEmailAddressFromDatabase.prototype = {
    initialize: function() {},
    getEmail: function(id) {
        var email = [];
        if (id) {
            var userRecord = new GlideRecord('sys_user');
            userRecord.addQuery("sys_id", id);
            userRecord.query();
            while (userRecord.next()) {
                var obj = {
                    "email": userRecord.getValue('email'),
                    "UserID": userRecord.getValue('user_name'),
                    "Sucess": true,
                    "Error": ""
                };
                email.push(obj);
            }
        } else {
            var objErr = {
                "email": "",
                "UserID": "",
                "Sucess": false,
                "Error": "Parameter is null"
            };
            email.push(obj);
        }
        return email;
    },

    type: 'GetEmailAddressFromDatabase'
};
